import{_ as e,c as i,o as l,V as o}from"./chunks/framework.lTxkm41A.js";const x=JSON.parse('{"title":"Linux 常用命令","description":"","frontmatter":{},"headers":[],"relativePath":"views/learningNotes/linux.md","filePath":"views/learningNotes/linux.md"}'),a={name:"views/learningNotes/linux.md"},c=o('<h1 id="linux-常用命令" tabindex="-1">Linux 常用命令 <a class="header-anchor" href="#linux-常用命令" aria-label="Permalink to &quot;Linux 常用命令&quot;">​</a></h1><h2 id="一、文件和目录操作" tabindex="-1">一、文件和目录操作 <a class="header-anchor" href="#一、文件和目录操作" aria-label="Permalink to &quot;一、文件和目录操作&quot;">​</a></h2><ul><li>列出目录内容：<code>ls</code></li><li>切换目录：<code>cd</code></li><li>显示当前目录：<code>pwd</code></li><li>创建一个新目录：<code>mkdir docs</code></li><li>创建一个空文件或更新文件的时间戳：<code>touch example.txt</code></li><li>查找文件：<code>find . -name report.txt</code></li><li>复制文件或目录：<code>cp file1.txt backup/</code></li><li>移动文件或目录：<code>mv file2.txt documents/</code></li><li>删除文件或目录：<code>rm oldfile.txt</code></li><li>查看文件内容：<code>cat example.txt</code></li><li>压缩目录为 tar.gz 文件：<code>tar -czvf file.tar.gz /path/to/directory</code></li><li>解压 tar.gz 文件：<code>tar -xzvf file.tar.gz</code></li><li>压缩文件为 .gz 格式：<code>gzip filename</code></li><li>解压 .gz 文件：<code>gzip -d filename.gz</code></li><li>压缩目录为 zip 文件：<code>zip -r archive.zip /path/to/directory</code></li><li>解压 zip 文件：<code>unzip archive.zip</code></li></ul><h2 id="二、系统操作" tabindex="-1">二、系统操作 <a class="header-anchor" href="#二、系统操作" aria-label="Permalink to &quot;二、系统操作&quot;">​</a></h2><ul><li>查看系统资源占用情况：<code>top</code></li><li>显示当前进程状态：<code>ps</code></li><li>终止进程：<code>kill</code></li><li>重启系统：<code>reboot</code></li><li>关闭系统：<code>shutdown</code></li></ul><h2 id="三、用户管理" tabindex="-1">三、用户管理 <a class="header-anchor" href="#三、用户管理" aria-label="Permalink to &quot;三、用户管理&quot;">​</a></h2><ul><li>添加新用户：<code>useradd username</code></li><li>设置用户密码：<code>passwd username</code></li><li>切换用户：<code>su username</code></li><li>以管理员权限执行命令：<code>sudo</code></li><li>删除用户：<code>userdel</code></li></ul><h2 id="四、网络操作" tabindex="-1">四、网络操作 <a class="header-anchor" href="#四、网络操作" aria-label="Permalink to &quot;四、网络操作&quot;">​</a></h2><ul><li>检测与目标主机的连通性：<code>ping target_host</code></li><li>显示或配置网络设备：<code>ifconfig</code></li><li>远程登录到另一台主机：<code>ssh username@hostname</code></li><li>下载文件：<code>wget https://example.com/file.zip</code></li></ul><h2 id="vim-编辑器的命令" tabindex="-1">Vim 编辑器的命令 <a class="header-anchor" href="#vim-编辑器的命令" aria-label="Permalink to &quot;Vim 编辑器的命令&quot;">​</a></h2><ul><li>打开Vim编辑器：打开名为example.txt的文件进行编辑 <code>vim example.txt</code></li><li>保存并退出：按下<code>Esc</code>键进入命令模式，然后输入<code>:wq</code>（保存并退出）</li><li>退出不保存：按下<code>Esc</code>键进入命令模式，然后输入<code>:q!</code>（退出不保存）</li><li>编辑（插入）模式：按下<code>i</code>键即可开始输入文本</li><li>查找文本：按下<code>Esc</code>键进入命令模式，然后输入<code>/search_term</code>来查找文本</li></ul><h2 id="more" tabindex="-1">More <a class="header-anchor" href="#more" aria-label="Permalink to &quot;More&quot;">​</a></h2><ul><li><a href="https://www.linux.org/" target="_blank" rel="noreferrer">Linux 操作系统 官网</a></li><li><a href="https://www.kernel.org/" target="_blank" rel="noreferrer">Linux 核心(内核) 官网</a></li></ul>',13),d=[c];function t(r,n,s,h,u,m){return l(),i("div",null,d)}const f=e(a,[["render",t]]);export{x as __pageData,f as default};
